import{_ as e,a as r}from"./chunks/webssh.C2tqG0xr.js";import{_ as a,c as t,o,a4 as i}from"./chunks/framework.BmdFiWrL.js";const k=JSON.parse('{"title":"Broker for Nezha - 拓展 Nezha 接入平台","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"case/case7.md","filePath":"case/case7.md","lastUpdated":1740049170000}'),l={name:"case/case7.md"},n=i('<h1 id="broker-for-nezha-拓展-nezha-接入平台" tabindex="-1">Broker for Nezha - 拓展 Nezha 接入平台 <a class="header-anchor" href="#broker-for-nezha-拓展-nezha-接入平台" aria-label="Permalink to &quot;Broker for Nezha - 拓展 Nezha 接入平台&quot;">​</a></h1><p>贡献者:</p><ul><li><a href="https://codeberg.org/uubulb" target="_blank" rel="noreferrer">uubulb</a></li></ul><p>Broker for Nezha 是一个 Nezha Agent 的修改版本，其通过分离 Agent 的数据收集和 gRPC 上报功能，可使小型设备接入 Nezha Dashboard。</p><p>项目地址：<a href="https://codeberg.org/uubulb/broker" target="_blank" rel="noreferrer">https://codeberg.org/uubulb/broker</a></p><p>文档：<a href="https://broker.kuzu.uk/" target="_blank" rel="noreferrer">Broker for Nezha</a></p><h2 id="与原项目的不同" tabindex="-1">与原项目的不同 <a class="header-anchor" href="#与原项目的不同" aria-label="Permalink to &quot;与原项目的不同&quot;">​</a></h2><ul><li>使用外置数据源，而不是从本地获取。</li><li>多数据源与多面板支持</li><li>仅支持部分任务类型，可参考项目 README 获取详细信息。</li></ul><p>Broker for Nezha 需要与特定的数据收集软件一同使用。目前提供两种数据获取方式：</p><ol><li>HTTP 主动获取（HTTP/1.1）</li><li>TCP 被动获取（推荐）</li></ol><p>如需获取现有的数据收集软件，可以参考项目 README。</p><p>因数据获取方式缺乏安全性，建议仅在信任的网络环境使用。</p><h2 id="编写数据收集端" tabindex="-1">编写数据收集端 <a class="header-anchor" href="#编写数据收集端" aria-label="Permalink to &quot;编写数据收集端&quot;">​</a></h2><p>如需自行编写数据收集端，除实现基本 HTTP 服务器或 TCP 客户端外，还需要使用特定的数据格式才可被 Broker 正常接收。具体请参考：<a href="https://broker.kuzu.uk/configuration/type/" target="_blank" rel="noreferrer">数据类型</a></p><h2 id="效果参考" tabindex="-1">效果参考 <a class="header-anchor" href="#效果参考" aria-label="Permalink to &quot;效果参考&quot;">​</a></h2><figure><img src="'+e+'" alt="Dashboard"><figcaption style="font-size:0.9em;color:gray;text-align:center;"> Dashboard </figcaption></figure><br><figure><img src="'+r+'" alt="WebSSH"><figcaption style="font-size:0.9em;color:gray;text-align:center;"> WebSSH </figcaption></figure>',18),s=[n];function h(c,p,u,f,_,b){return o(),t("div",null,s)}const m=a(l,[["render",h]]);export{k as __pageData,m as default};
