import{_ as e,c as o,o as t,a4 as a}from"./chunks/framework.BmdFiWrL.js";const m=JSON.parse('{"title":"Serverless Telegram Bot for Nezha","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"en_US/case/case8.md","filePath":"en_US/case/case8.md","lastUpdated":1756917506000}'),r={name:"en_US/case/case8.md"},l=a('<h1 id="serverless-telegram-bot-for-nezha" tabindex="-1">Serverless Telegram Bot for Nezha <a class="header-anchor" href="#serverless-telegram-bot-for-nezha" aria-label="Permalink to &quot;Serverless Telegram Bot for Nezha&quot;">​</a></h1><p>Contributor:</p><ul><li><a href="https://codeberg.org/uubulb" target="_blank" rel="noreferrer">uubulb</a></li></ul><p>Project URL: <a href="https://github.com/swkisdust/plain-nezha-bot" target="_blank" rel="noreferrer">https://github.com/swkisdust/plain-nezha-bot</a></p><p>A single-user, single-instance, multi-language private chat Nezha Telegram bot that can be deployed on Cloudflare Workers.</p><p>Inspired by <a href="https://github.com/nezhahq/Nezha-Telegram-Bot-V1" target="_blank" rel="noreferrer">https://github.com/nezhahq/Nezha-Telegram-Bot-V1</a></p><h2 id="deployment" tabindex="-1">Deployment <a class="header-anchor" href="#deployment" aria-label="Permalink to &quot;Deployment&quot;">​</a></h2><ol><li>Clone this project and run <code>npm install</code> to install dependencies.</li><li>Create a Workers KV namespace using your preferred method and save its ID.</li><li>Modify <code>wrangler.toml</code> and update the <code>kv_namespaces</code> field with your KV information.</li><li>Modify <code>wrangler.toml</code> under <code>vars</code>:</li></ol><ul><li><code>NZ_BASEURL</code>: Panel address, e.g., <code>https://qio.ng</code>.</li><li><code>LANG</code>: Language, options are <code>en</code> and <code>zh-CN</code>, default is <code>en</code>.</li><li><code>ENDPOINT_PATH</code>: Route path for receiving Telegram Webhook, e.g., <code>/endpoint</code>.</li></ul><ol start="5"><li>Create the following Secrets:</li></ol><ul><li><code>TELEGRAM_BOT_TOKEN</code>: Bot token obtained from BotFather.</li><li><code>TELEGRAM_SECRET</code>: Webhook authentication key.</li><li><code>TELEGRAM_UID</code>: User UID, the bot will not interact with anyone other than this UID.</li><li><code>PASSWORD</code>: Basic authentication password for register/unregister/refresh token operations.</li><li><code>NZ_USERNAME</code>: Panel username for initial authentication and subsequent refreshes.</li><li><code>NZ_PASSWORD</code>: Panel password for initial authentication and subsequent refreshes.</li></ul><ol start="6"><li>Run <code>npx wrangler deploy</code> to deploy the project.</li></ol><h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to &quot;Usage&quot;">​</a></h2><p>Access the <code>/register</code> route to register the Webhook and start using the bot.</p><p>If you do not need the bot temporarily, you can access <code>/unregister</code> to remove the Webhook.</p><p>By default, the token refresh operation is triggered every 30 minutes. You can modify this manually in <code>wrangler.toml</code> or access <code>/refresh</code> to refresh manually.</p>',16),n=[l];function i(s,d,c,h,u,p){return t(),o("div",null,n)}const f=e(r,[["render",i]]);export{m as __pageData,f as default};
