import{_ as e,c as t,o,a4 as a}from"./chunks/framework.BmdFiWrL.js";const g=JSON.parse('{"title":"How to Migrate, Backup, and Restore Data?","description":"","frontmatter":{},"headers":[],"relativePath":"en_US/guide/q5.md","filePath":"en_US/guide/q5.md","lastUpdated":1740229534000}'),r={name:"en_US/guide/q5.md"},s=a('<h1 id="how-to-migrate-backup-and-restore-data" tabindex="-1">How to Migrate, Backup, and Restore Data? <a class="header-anchor" href="#how-to-migrate-backup-and-restore-data" aria-label="Permalink to &quot;How to Migrate, Backup, and Restore Data?&quot;">​</a></h1><p>Follow these steps to migrate, backup, or restore Nezha Monitoring data:</p><div class="danger custom-block"><p class="custom-block-title">DANGER</p><p>The database structure between V0 and V1 is incompatible. <strong>Do not use this method for cross-version upgrades from V0 to V1.</strong></p></div><ol><li><p><strong>Stop the Dashboard Service</strong><br> Run the one-click script on the old server and select <code>Stop Dashboard</code>.</p></li><li><p><strong>Backup Data</strong><br> Compress the <code>/opt/nezha</code> folder on the old server and copy it to the same location on the new server.</p></li><li><p><strong>Restore Data</strong><br> Extract the <code>/opt/nezha</code> folder to the same path on the new server.</p></li><li><p><strong>Start the Dashboard Service</strong><br> Run the one-click script on the new server and select <code>Start Dashboard</code>.</p></li></ol><hr><h3 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-label="Permalink to &quot;Notes&quot;">​</a></h3><ul><li>Ensure that the <code>/opt/nezha</code> folder and its contents have the correct permissions and ownership on the new server.</li><li>After the migration is complete, verify its success by accessing the Dashboard interface.</li></ul>',7),n=[s];function c(i,d,h,l,p,u){return o(),t("div",null,n)}const m=e(r,[["render",c]]);export{g as __pageData,m as default};
